<?phpsession_start();if (isset($_GET['log'])) {    $file = 'db/members.txt';    $users = unserialize(file_get_contents($file));    $userInfo = &$users[$_SESSION['user']];    saveInfo($userInfo, 'additionTotal');    saveInfo($userInfo, 'substractionTotal');    saveInfo($userInfo, 'multiplicationTotal');    saveInfo($userInfo, 'additionScore');    saveInfo($userInfo, 'substractionScore');    saveInfo($userInfo, 'multiplicationScore');    saveInfo($userInfo, 'operationTotal');    saveInfo($userInfo, 'scoreTotal');    file_put_contents($file, serialize($users));    unset($_SESSION['user']);    session_destroy();}function saveInfo(&$a, $key) {    $a[$key] += $_SESSION[$key];}?><!DOCTYPE html><html lang = "en"><head>    <link rel="stylesheet" href="css/A3_1.css"></head><body>    <?php include 'header.php'; ?>    <?php include 'menu.php'; ?>    <section></section>    <?php include 'footer.php'; ?></body></html>