<?phpsession_start();$user = isset($_POST['user']) ? $_POST['user'] : '';if ($_SERVER["REQUEST_METHOD"] == "POST") {    if (isset($_POST['submit']) && (isset($_POST['user']) & $_POST['user'] == '')) {        alert('Username is empty!');    } elseif (isset($_POST['submit'])) {        // open file and check for username        $file = 'db/members.txt';        $users = unserialize(file_get_contents($file));        $userInfo = $users[$user];        if (array_key_exists($user, $users)) {            if ($_POST['pass'] == $userInfo['passwrd']) {                $_SESSION['user'] = $user;                loadInfo($userInfo, 'additionTotal');                loadInfo($userInfo, 'substractionTotal');                loadInfo($userInfo, 'multiplicationTotal');                loadInfo($userInfo, 'additionScore');                loadInfo($userInfo, 'substractionScore');                loadInfo($userInfo, 'multiplicationScore');                loadInfo($userInfo, 'operationTotal');                loadInfo($userInfo, 'scoreTotal');                $page = isset($_GET['request']) ? $_GET['request'] : 'index.php';                header('Location: ' . $page); // redirect to requested page and logged in                die();            } else {                alert('Password is incorrect!');            }        } else {            alert('Username does not exist!');        }    }}function loadInfo($a, $key) {    $_SESSION[$key] = $a[$key];}function alert($message) {    echo "<script type='text/javascript'>alert('$message');</script>";}?><!DOCTYPE html><html lang = "en"><head>    <link rel="stylesheet" href="css/A3_1.css"></head><body><?php include 'header.php';?><?php include 'menu.php'; ?>    <div class = "right_content">        <form action="" method="post">            <fieldset>            <p>            <label for="user">Login name:</label>            <input type="text" name="user" id="user" value="<?php echo $user; ?>" />            <label for="pass">Password:</label>            <input type="password" name="pass" id="pass" value="" />            <input type="submit" value="Login" name="submit" id="submit" /><br /><br />        </form>    </div><?php include 'footer.php';?></body></html>