<?phpinclude 'security.php';$file = 'db/members.txt';$users = unserialize(file_get_contents($file));$userInfo =  $users[$_SESSION['user']];function echo_li($op, $number, $score) {    echo '<li>Number of ' . $op . ' attempted: ' . $number . ' and score: ' . $score . '</li>';}?><!DOCTYPE html><html lang = "en"><head>   <link rel="stylesheet" href="css/A3_1.css"></head><body>  <?php include 'header.php';?>  <?php include 'menu.php'; ?>  <section id="contact">    <h1>My Statistics</h1>    <p>Stats for current session</p>    <ul>        <?php        echo_li('additions', $_SESSION['additionTotal'], $_SESSION['additionScore']);        echo_li('substractions', $_SESSION['substractionTotal'], $_SESSION['substractionScore']);        echo_li('multiplications', $_SESSION['multiplicationTotal'], $_SESSION['multiplicationScore']);        echo_li('total operations', $_SESSION['operationTotal'], $_SESSION['scoreTotal']);        ?>    </ul>    <p>Cumulative Stats</p>    <ul>        <?php        echo_li('additions', $userInfo['additionTotal'], $userInfo['additionScore']);        echo_li('substractions', $userInfo['substractionTotal'], $userInfo['substractionScore']);        echo_li('multiplications', $userInfo['multiplicationTotal'], $userInfo['multiplicationScore']);        echo_li('total operations', $userInfo['operationTotal'], $userInfo['scoreTotal']);        ?>    </ul></section><?php include 'footer.php';?></body></html>