<?phpsession_start();$user = isset($_POST['user']) ? $_POST['user'] : '';if ($_SERVER["REQUEST_METHOD"] == "POST") {	if(isset($_POST['submit']) && (isset($_POST['user']) & $_POST['user'] == '')) {		alert('Username is empty!');	} elseif(isset($_POST['submit'])) {		// open file and check for username		$file = '../db/members.txt';		$users = unserialize(file_get_contents($file));		if (array_key_exists($user, $users)) {			if ($_POST['pass'] == $users[$user]['passwrd']) {				$_SESSION['user'] = $user;				header('Location: ../index.php'); // redirect to main page and logged in				die();			} else {				alert('Password is incorrect!');			}		} else {			alert('Username does not exist!');		}	}}function alert($message) {	echo "<script type='text/javascript'>alert('$message');</script>";}?><!DOCTYPE html><html lang = "en"><head>	<link rel="stylesheet" href="../css/A3_1.css"></head><html><head><title>login</title><link rel="stylesheet" type="text/css" href="style/style.css" /></head><body><?php include 'header.php';?>	<nav>		<ul>			<li><a href="index.php">Home</a></li>			<li><a href="register.php">Register</a></li>			<li>Quiz Me				<ul>					<li><a href="php/addition.php">&raquo; Addition</a></li>					<li><a href="php/subtraction.php">&raquo; Subtraction</a></li>					<li><a href="php/multiplication.php">&raquo; Multiplication</a></li>					<li><a href="php/mix.php">&raquo; Mix</a></li>				</ul>			</li>			<li><a href="php/myStats.php">My stats</a></li>			<li><a href="php/contact.php">Contact us</a></li>		</ul>	</nav>	<div class = "right_content">		<!-- See http://www.w3schools.com/php/php_form_validation.asp -->		<form action="<?php echo htmlspecialchars($_SERVER["PHP_SELF"]);?>" method="post">			<fieldset>			<p>			<label for="user">Login name:</label>			<input type="text" name="user" id="user" value="<?php echo $user; ?>" />			<label for="pass">Password:</label>			<input type="password" name="pass" id="pass" value="" />	